	$arrPerm = getTablePermissions('budgets');
	if(!$arrPerm['insert']) return false;

	$data = [
		'convention' => Request::lookup('convention', ''),
		'type' => Request::lookup('type', ''),
		'precision' => Request::val('precision', ''),
		'accorde' => Request::val('accorde', ''),
