	$templateCode .= loadView('budgets-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: budgets_dv
	if(function_exists('budgets_dv')) {
		$args=[];
		budgets_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}