		exit;
	}

	// hook: conventions_before_insert
	if(function_exists('conventions_before_insert')) {
		$args = [];
		if(!conventions_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('conventions', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('conventions', $recID, calculated_fields()['conventions']);

	// hook: conventions_after_insert
	if(function_exists('conventions_after_insert')) {
		$res = sql("SELECT * FROM `conventions` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!conventions_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

