function conventions_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('conventions', $selected_id, 'edit')) return false;

	$data = [
		'nom' => Request::val('nom', ''),
		'statut' => Request::val('statut', ''),
		'bailleur' => Request::val('bailleur', ''),
		'porteur' => Request::lookup('porteur', ''),
		'chef_projet' => Request::lookup('chef_projet', ''),
		'date_reponse' => Request::dateComponents('date_reponse', ''),
		'demande' => Request::val('demande', ''),
		'date_debut' => Request::dateComponents('date_debut', ''),
		'date_fin' => Request::dateComponents('date_fin', ''),
		'duree' => Request::val('duree', ''),
		'notes' => Request::val('notes', ''),
		'accorde_hfg' => Request::val('accorde_hfg', ''),
		'frais_gestion' => Request::val('frais_gestion', ''),
		'accorde' => Request::val('accorde', ''),
		'verse' => Request::val('verse', ''),
		'reste_verser' => Request::val('reste_verser', ''),
		'verse_hfg' => Request::val('verse_hfg', ''),
		'ouvert' => Request::val('ouvert', ''),
		'reste_ouvrir' => Request::val('reste_ouvrir', ''),
		'non_liquide' => Request::val('non_liquide', ''),
		'liquide' => Request::val('liquide', ''),
		'utilise' => Request::val('utilise', ''),
		'disponible' => Request::val('disponible', ''),
		'reste_engager' => Request::val('reste_engager', ''),
		'reservation_salaire' => Request::val('reservation_salaire', ''),
		'reste_depenser' => Request::val('reste_depenser', ''),
		'prop_uo' => Request::val('prop_uo', ''),
		'prop_uv' => Request::val('prop_uv', ''),
		'prop_ua' => Request::val('prop_ua', ''),
		'budget_nv' => Request::val('budget_nv', ''),
		'depenses_nv' => Request::val('depenses_nv', ''),
	];

	if($data['nom'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nom': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['statut'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Statut': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('conventions', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: conventions_before_update
	if(function_exists('conventions_before_update')) {
		$args = ['old_data' => $old_data];
		if(!conventions_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'conventions', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="conventions_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('conventions', $data['selectedID'], calculated_fields()['conventions']);

	// hook: conventions_after_update
	if(function_exists('conventions_after_update')) {
		$res = sql("SELECT * FROM `conventions` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!conventions_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='conventions' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

