function personnes_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('personnes');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['nom'] = $_REQUEST['nom'];
		if($data['nom'] == empty_lookup_value) { $data['nom'] = ''; }
	$data['email'] = $_REQUEST['email'];
		if($data['email'] == empty_lookup_value) { $data['email'] = ''; }
	if($data['nom']== '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Pr&#233;nom Nom': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: personnes_before_insert
	if(function_exists('personnes_before_insert')) {
		$args = array();
		if(!personnes_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('personnes', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: personnes_after_insert
	if(function_exists('personnes_after_insert')) {
		$res = sql("select * from `personnes` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!personnes_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('personnes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) personnes_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function personnes_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = array(); // array of curl handlers for launching insert requests
	$eo = array('silentErrors' => true);
	$uploads_dir = realpath(dirname(__FILE__) . '/../' . $Translation['ImageFolder']);
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

