	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('personnes');
	if($selected_id) {
		$jdata = get_joined_record('personnes', $selected_id);
		if($jdata === false) $jdata = get_defaults('personnes');
		$rdata = $row;
	}
	$templateCode .= loadView('personnes-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: personnes_dv
	if(function_exists('personnes_dv')) {
		$args=array();
		personnes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>