	$data['reservation_salaire'] = $_REQUEST['reservation_salaire'];
		if($data['reservation_salaire'] == empty_lookup_value) { $data['reservation_salaire'] = ''; }
	$data['prop_dp'] = $_REQUEST['prop_dp'];
		if($data['prop_dp'] == empty_lookup_value) { $data['prop_dp'] = ''; }
	if($data['convention']== '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Convention': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
