		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: rubriques_after_insert
	if(function_exists('rubriques_after_insert')) {
		$res = sql("select * from `rubriques` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
