function rubriques_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('rubriques');
	if(!$arrPerm['insert']) return false;

	$data = [
		'convention' => Request::lookup('convention', ''),
		'intitule' => Request::val('intitule', ''),
		'notes' => Request::val('notes', ''),
		'accorde' => Request::val('accorde', ''),
		'non_liquide' => Request::val('non_liquide', ''),
		'liquide' => Request::val('liquide', ''),
		'utilise' => Request::val('utilise', ''),
		'reste_engager' => Request::val('reste_engager', ''),
		'reservation_salaire' => Request::val('reservation_salaire', ''),
		'reste_depenser' => Request::val('reste_depenser', ''),
		'prop_ua' => Request::val('prop_ua', ''),
	];

	if($data['convention'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Convention': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['intitule'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Intitul&#233;': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: rubriques_before_insert
	if(function_exists('rubriques_before_insert')) {
		$args = [];
		if(!rubriques_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('rubriques', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('rubriques', $recID, calculated_fields()['rubriques']);

	// hook: rubriques_after_insert
	if(function_exists('rubriques_after_insert')) {
		$res = sql("SELECT * FROM `rubriques` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!rubriques_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('rubriques', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) rubriques_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function rubriques_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function rubriques_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('rubriques', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: rubriques_before_delete
	if(function_exists('rubriques_before_delete')) {
		$args = [];
		if(!rubriques_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	// child table: ventilation
	$res = sql("SELECT `id` FROM `rubriques` WHERE `id`='{$selected_id}'", $eo);
	$id = db_fetch_row($res);
	$rires = sql("SELECT COUNT(1) FROM `ventilation` WHERE `rubrique`='" . makeSafe($id[0]) . "'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'ventilation', $RetMsg);
		return $RetMsg;
	} elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation['confirm delete'];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'ventilation', $RetMsg);
		$RetMsg = str_replace('<Delete>', '<input type="button" class="button" value="' . $Translation['yes'] . '" onClick="window.location = \'rubriques_view.php?SelectedID=' . urlencode($selected_id) . '&delete_x=1&confirmed=1\';">', $RetMsg);
		$RetMsg = str_replace('<Cancel>', '<input type="button" class="button" value="' . $Translation[ 'no'] . '" onClick="window.location = \'rubriques_view.php?SelectedID=' . urlencode($selected_id) . '\';">', $RetMsg);
		return $RetMsg;
	}

	sql("DELETE FROM `rubriques` WHERE `id`='{$selected_id}'", $eo);

	// hook: rubriques_after_delete
	if(function_exists('rubriques_after_delete')) {
		$args = [];
		rubriques_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='rubriques' AND `pkValue`='{$selected_id}'", $eo);
}

function rubriques_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('rubriques', $selected_id, 'edit')) return false;

	$data = [
		'convention' => Request::lookup('convention', ''),
		'intitule' => Request::val('intitule', ''),
		'notes' => Request::val('notes', ''),
		'accorde' => Request::val('accorde', ''),
		'non_liquide' => Request::val('non_liquide', ''),
		'liquide' => Request::val('liquide', ''),
		'utilise' => Request::val('utilise', ''),
		'reste_engager' => Request::val('reste_engager', ''),
		'reservation_salaire' => Request::val('reservation_salaire', ''),
		'reste_depenser' => Request::val('reste_depenser', ''),
		'prop_ua' => Request::val('prop_ua', ''),
	];

	if($data['convention'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Convention': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['intitule'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Intitul&#233;': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('rubriques', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: rubriques_before_update
	if(function_exists('rubriques_before_update')) {
		$args = ['old_data' => $old_data];
		if(!rubriques_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'rubriques', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="rubriques_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('rubriques', $data['selectedID'], calculated_fields()['rubriques']);

	// hook: rubriques_after_update
	if(function_exists('rubriques_after_update')) {
		$res = sql("SELECT * FROM `rubriques` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!rubriques_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='rubriques' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

